INSERT INTO `PRODUCTOS` VALUES (1,'la buenizzima','Especial de la casa. La Buenizzima es una mezcla de sabores que no vas a poder quitarte de la cabeza.','especialidades',17.99,'media/images/SF/pizza_la_buenizzima_SF.webp','pizzas'),(2,'margarita','Clásica y sencilla que combina sabores frescos y auténticos como la albahaca, el tomate y la mozzarella, ofreciendo una experiencia deliciosa y equilibrada.','especialidades',14.99,'media/images/SF/pizza_margarita_SF.webp','pizzas'),(3,'barbacoa cremosa','Llena de sabor, combinando la dulzura de la salsa barbacoa con ingredientes ahumados y queso derretido para una experiencia intensa y satisfactoria.','clasicas',15.99,'media/images/SF/pizza_barbacoa_SF.webp','pizzas'),(4,'carbonara','Cremosa y reconfortante, que mezcla el sabor suave de la salsa blanca, el queso y el toque salado del bacon, creando una combinación deliciosa y rica en texturas.','clasicas',15.99,'media/images/SF/pizza_carbonara_SF.webp','pizzas'),(5,'hawaiana','Cremosa y reconfortante, que mezcla el sabor suave de la salsa blanca, el queso y el toque salado del bacon, creando una combinación deliciosa y rica en texturas.','clasicas',15.99,'media/images/SF/pizza_hawaiana_SF.webp','pizzas'),(6,'cuatro quesos','Festín cremoso y lleno de sabor, donde la mezcla de distintos quesos se funde a la perfección, ofreciendo una experiencia rica y deliciosa para los amantes del queso.','clasicas',14.99,'media/images/SF/pizza_cuatro_quesos_SF.webp','pizzas'),(7,'bacon','Indulgente y sabrosa, donde el crujiente y ahumado bacon se combina con queso derretido para un bocado lleno de sabor y textura.','clasicas',13.99,'media/images/SF/pizza_bacon_SF.webp','pizzas'),(8,'peperoni','Explosión de sabor con el irresistible toque ahumado y especiado del pepperoni, creando una experiencia deliciosa y con un toque atrevido.','clasicas',13.99,'media/images/SF/pizza_peperoni_SF.webp','pizzas'),(9,'la furiosa','Audaz y explosiva, cargada de ingredientes picantes que intensifican cada